import React, { Component } from 'react';
import './App.css';
import ReactDOM from 'react-dom';
import { Router, Route, Link, browserHistory, IndexRoute } from 'react-router'
import withRouter from 'react-router-dom';

window.axios.defaults.baseURL = "http://127.0.0.1:8000/coreback/rest-auth/";

const Public = () => <h3>Public</h3>
const Protected = () => <h3>Protected</h3>

class App extends Component {
  constructor () {
    super();
    this.state = {
      email: '',
      password: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange (evt) {
    this.setState({ [evt.target.name]: evt.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(this.state.email);
    console.log(this.state.password);
    window.axios.post('login/',{ //TODO:add CORS headers
      email:this.state.email,
      password:this.state.password
    })
    .then(function (response) {
      if("key" in response.data){
        window.axios.defaults.headers.common['Authorization'] = response.data.key;
        alert("success");
        withRouter(({history}) => history.push('/protected'));
      }
    })
    .catch(function (error) {
      console.log(error.response);
      if(error.response.status === 400){
        if("email" in error.response.data){
          alert("Please provide a valid email address");
        }
        else{
          alert("Unable to login with provided credentials");
        }
      }
    });
    this.setState({
      invalid: false,
    });
  }

  render () {
    return (
      <form onSubmit={this.handleSubmit}>

        <label>Email</label>
        <input type="text" name="email" onChange={this.handleChange} />
        <br/>
        <label>Password</label>
        <input type="password" name="password" onChange={this.handleChange} />
        <br/>
        <input type="submit" value="Submit" />

      </form>
    );
  }
}

export default function AuthExample () {
  return (
    <Router>
      <div>
        <AuthButton/>
        <ul>
          <li><Link to="/public">Public Page</Link></li>
          <li><Link to="/protected">Protected Page</Link></li>
        </ul>
        <Route path="/public" component={Public}/>
        <Route path="/protected" component={Protected}/>
        <Route path="/login" component={App}/>
      </div>
    </Router>
  )
}
