import React, { Component } from 'react';
import './App.css';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect,
  withRouter
} from "react-router-dom";
import Profile from "./profile";

window.axios.defaults.baseURL = "http://127.0.0.1:8000/coreback/rest-auth/";

class App extends Component {
  constructor () {
    super();
    this.state = {
      email: '',
      password: ''
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange (evt) {
    this.setState({ [evt.target.name]: evt.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    console.log(this.state.email);
    console.log(this.state.password);
    window.axios.post('login/',{ //TODO:add CORS headers
      email:this.state.email,
      password:this.state.password
    })
    .then(function (response) {
      if("key" in response.data){
        window.axios.defaults.headers.common['Authorization'] = response.data.key;
        alert("success");
      }
    })
    .catch(function (error) {
      console.log(error.response);
      if(error.response.status === 400){
        if("email" in error.response.data){
          alert("Please provide a valid email address");
        }
        else{
          alert("Unable to login with provided credentials");
        }
      }
    });
    this.setState({
      invalid: false,
    });
  }

  render () {
    return (
      <div>
        <Switch>
          <Route path="/profile" component={Profile} />
        </Switch>
        <form onSubmit={this.handleSubmit}>

          <label>Email</label>
          <input type="text" name="email" onChange={this.handleChange} />
          <br/>
          <label>Password</label>
          <input type="password" name="password" onChange={this.handleChange} />
          <br/>
          <input type="submit" value="Submit" />

        </form>
      </div>
    );
  }
}

export default App;
